PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE jobs (
	id INTEGER NOT NULL, 
	job_id VARCHAR NOT NULL, 
	service_name VARCHAR NOT NULL, 
	status VARCHAR(9) NOT NULL, 
	input_data JSON, 
	output_data JSON, 
	error_message TEXT, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	started_at DATETIME, 
	completed_at DATETIME, 
	PRIMARY KEY (id)
);
CREATE TABLE device_connections (
	id INTEGER NOT NULL, 
	device_id VARCHAR NOT NULL, 
	device_name VARCHAR NOT NULL, 
	device_type VARCHAR, 
	last_seen DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	metadata JSON, 
	is_connected VARCHAR, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id)
);
CREATE TABLE collected_data (
	id INTEGER NOT NULL, 
	source VARCHAR NOT NULL, 
	data_type VARCHAR NOT NULL, 
	data JSON NOT NULL, 
	collected_at DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	expires_at DATETIME, 
	PRIMARY KEY (id)
);
CREATE TABLE chat_messages (
	id INTEGER NOT NULL, 
	session_id VARCHAR, 
	role VARCHAR NOT NULL, 
	message TEXT NOT NULL, 
	service_name VARCHAR, 
	metadata JSON, 
	created_at DATETIME DEFAULT (CURRENT_TIMESTAMP), mode VARCHAR, persona VARCHAR, 
	PRIMARY KEY (id)
);
CREATE TABLE device_telemetry (
	id INTEGER NOT NULL, 
	device_id VARCHAR NOT NULL, 
	metric_name VARCHAR NOT NULL, 
	value JSON NOT NULL, 
	unit VARCHAR, 
	timestamp DATETIME DEFAULT (CURRENT_TIMESTAMP), 
	PRIMARY KEY (id)
);
CREATE INDEX ix_jobs_id ON jobs (id);
CREATE UNIQUE INDEX ix_jobs_job_id ON jobs (job_id);
CREATE INDEX ix_device_connections_id ON device_connections (id);
CREATE UNIQUE INDEX ix_device_connections_device_id ON device_connections (device_id);
CREATE INDEX ix_collected_data_source ON collected_data (source);
CREATE INDEX ix_collected_data_id ON collected_data (id);
CREATE INDEX ix_chat_messages_session_id ON chat_messages (session_id);
CREATE INDEX ix_chat_messages_id ON chat_messages (id);
CREATE INDEX ix_device_telemetry_id ON device_telemetry (id);
CREATE INDEX ix_device_telemetry_timestamp ON device_telemetry (timestamp);
CREATE INDEX ix_device_telemetry_metric_name ON device_telemetry (metric_name);
CREATE INDEX ix_device_telemetry_device_id ON device_telemetry (device_id);
COMMIT;
